String[] kv;

HashMap<String, String> params = new HashMap<String, String>()
for (int i = 0; i < fields.length; ++i){
    kv = fields[i].split("=");
    if (2 == kv.length)
     params.put(kv[0], kv[1]); 
}

def username = params.get("username")
def password = params.get("password")
def sum = username + password

// validate
